INSERT INTO `kata_pengantars` (`id`, `judul`, `isi`, `deskripsi`, `jabatan`, `foto`, `tokoh`, `created_at`, `updated_at`) VALUES
(1, 'KATA SAMBUTAN REKTOR  UNIVERSITAS ISLAM NEGERI SUMATERA UTARA MEDAN', '<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:20px\">Puji dan syukur yang tiada henti-hentinya kita persembahkan kehadirat Allah <em>Subah&acirc;n&ucirc; wa Ta&rsquo;&acirc;l&acirc;</em> yang senantiasa memberikan rahmat dan taufik-Nya kepada kita hingga dapat melanjutkan pengabdian kepada-Nya melalui pengembangan peradaban dan kemanusiaan, khususnya dalam menjalankan tugas dan pengembangan Universitas Islam Negeri (UIN) Sumatera Utara.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:20px\">Dengan transformasi Institut Agama Islam Negeri (IAIN) menjadi Universitas Islam Negeri (UIN) Sumatera Utara, pengelolaan universitas ini terasa menghadapi tantangan yang sangat tidak ringan dan amat luas spektrumnya; bukan hanya kebutuhan pembangunan sarana dan prasarana yang demikian kompleks, tetapi--sejajar dengan atau lebih penting dari itu--perumusan dan menjalankan paradigma keilmuan yang bersifat integratif yang menjadi petunjuk (<em>guidance</em>) penerapannya bagi kesejahteraan umat manusia.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:20px\">Menyadari akan hal itulah kami merasa bersyukur kepada Allah dan berterima kasih yang tiada berhingga kepada Tim Perumus--yang dipimpin oleh Prof. Dr. Syahrin Hafrahap, MA&mdash;yang telah berhasil merumuskan paradigma pengembangan keilmuan di Universitas Islam Negeri (UIN) Sumatera Utara yang dirumuskan dalam term <em>Wahdatul &lsquo;Ul&ucirc;m</em> sebagaimana terdapat dalam buku ini.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:20px\">Perumusan paradigma pengembangan keilmuan ini menjadi suatu keniscayaan dalam pengembangan Universitas Islam Negeri (UIN) Sumatera Utara dengan tiga pertimbangan. <em>Pertama,</em> harapan masyarakat yang semakin tinggi kepada universitas ini untuk dapat mencetak sarjana yang memiliki ilmu pengetahuan yang bersifat integratif; bukan saja&nbsp; unggul dalam &lsquo;ilmu-ilmu keislaman&rsquo; tetapi juga unggul dalam pengembangan &lsquo;ilmu pengetahuan Islam&rsquo;, sehingga dapat memajukan umat dan mampu mengedepankan solusi bagi kebutuhan dan problema masyarakat yang memberi harapan akan keselamatan di dunia dan dihari kemudian.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:20px\"><em>Kedua,</em> universitas ini memiliki tanggung jawab sejarah yang sangat mulia; kalau pada kelahirannya di tahun 1973 harapan masyarakat terhadapnya agar dapat mencetak ulama, da&rsquo;i, dan pegawai Kemenrian Agama, maka harapan itu kini&mdash;setelah bertransformasi menjadi Universitas Islam--memiliki spectrum yang amat luas; bukan saja dapat mencetak ulama, d&acirc;&rsquo;i, dan pegawai Kementeria Agama, tetapi juga sebagai ilmuan yang ulama, ulama yang ilmuan, politisi yang beretika, teknokrat yang islami, dan tokoh masyarakat yang memiliki akhlak yang terpuji, dan kemampuan-kemampuan lain yang bersiaft integratif.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:20px\"><em>Ketiga</em>, paradigma pengembangan keilmuan dan <em>guidance</em> ini diharapkan menjadi penunjuk arah bagi keseluruhan pengembangan Tri Darma perguruan tinggi di Universitas Islam Negeri (UIN) Sumatera Utara.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:20px\">Sejalan dengan itu Universitas Islam Negeri (UIN) Sumatera Utara berketetapan hati menerapkan <em>Wahdatul &lsquo;Ulum</em> sebagai Paradigma Pengembanagan Keilmuan dan <em>&lsquo;Ulul &lsquo;Ilmi&rsquo;</em> sebagai profil dan karakter lulusannya.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:20px\">Penetapan ini merupakan upaya merealisasikan&nbsp; pesan yang terkandung dalam Undang-Undang Nomor 2 Tahun 2003 tentang Sistem Pendidikan Nasional, Standar Isi Pendidikan Tinggi (SIPT), mengacu pada substansi Kerangka Kualifikasi Nasional Indonesia (KKNI), dan ketentuan yang terdapat pada Keputusan Direktur Jenderal Pendidikan Islam Nomor 102 Tahun 2019 Tentang Standar Keagamaan Pendidikan Tinggi Agama Islam.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:20px\">Akhirnya, kami berharap dan meminta kepada seluruh civitas akademika Universitas Islam Negeri (UIN) Sumatera Utara agar dapat menginternalisasi paradigma dan <em>guidance</em> ini, serta dapat&nbsp; menerapkannya dalam seluruh kegiatan pengembangan keilmuan, penelitian, dan pengabdian kepada masyarata di Universitas ini.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:20px\">Semoga Allah Swt., meridhai usaha-usaha dan aktifitas kita dalam menjalankan dan mengembangkan Universitas tercinta ini.</span></span></p>', '<p>&quot;Perumusan paradigma integrasi keilmuan Ini merupakan suatu keniscayaan dalam pengembangan UIN Sumatera Utara Medan.&nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;Kami berharap dan meminta kepada seluruh civitas akademika &nbsp;UIN-SU agar dapat menginternalisasi paradigma dan guidance ini serta dapat menerapkannya dalam seluruh kegiatan pengembangan keilmuan, penelitian, dan pengabdian kepada masyarakat di Universitas ini.&quot;</p>', 'Rektor Universitas Islam Negeri Sumatera Utara Medan', '5dc851bbbdc6e.jpeg', 'Prof. Dr. Saidurrahman, M. Ag', '2019-07-07 17:20:01', '2019-11-10 18:06:53'),
(2, 'KATA SAMBUTAN KETUA SENAT  UNIVERSITAS ISLAM NEGERI SUMATERA UATARA MEDAN', '<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Puji dan syukur kita persembahkan kepada Allah Swt., yang senantiasa mencurahkan rahmat dan taufik-Nya kepada kita dalam menjalankan tugas dan pengabdian sehari-hari. Semoga seluruh aktifitas kita mendapat perkenan dari-Nya.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Salah satu tugas Senat Universitas Islam Negeri&nbsp; (UIN) Sumatera Utara adalah merumuskan paradigma pengembangan keilmuan dan petunjuk (<em>guidance</em>) penerapannya untuk dapat menghasilkan sarjana yang berpengetahuan integratif serta dapat berkontribusi bagi kesejahteraan umat manusia dan kemajuan bangsa.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Untuk itulah kami merasa bersyukur kepada Allah Swt., dan terima kasih kepada Komisi Pengembangan Keilmuan, Pengembangan Program Studi, dan Penerapan Transdisipliner, Senat Universitas Islam Negeri Sumatera Utara yang telah mengerjakan secara sungguh-sungguh&nbsp;Paradigma Pengembangan Keilmuan Universitas Islam Negeri (UIN) Sumatera Utara yang dirumuskan dalam term <em>Wahdatul Ulum</em>, sebagaimana terdapat dalam buku ini.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Paradigma Pengembangan Keilmuan tentulah sangat diperlukan dalam menentukan arah pengembangan keilmuan di Universitas ini agar para para pemimpin dapat mengelola unit yang dipimpinnya menuju arah yang telah dirumuskan; para dosen dapat mengembangkan ilmu dalam bidangnya sesuai dengan paradigm yang telah ditetapkan, tenaga administratif dan tenaga akademik dapat mendukung tugas-tugas untuk memberhasilkannya, mahasiswa dapat menjalankan tugas-tugas studinya sesuai arah yang telah ditetapkan, serta para user (pengguna lulusan) dapat mengukur penyerapan alumni sesuai dengan kebutuhannya.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Untuk itu Senat Universitas Islam Negeri Sumatera Utara berterima kasih kepada Rektor Universitas Islam Negeri (UIN) Sumatera Utara yang telah menetapkan paradigma <em>Wahdatul&nbsp; Ulum</em> sebagai pedoman dan <em>guidance</em> pengembangan keilmuan dan penerapannya di Universitas Islam Negeri Sumatera Utara.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Sekali lagi, kami mengucapkan terima kasih kepada Komisi Keilmuan Senat Universitas Islam Negeri (UIN) Sumatera Utara yang telah mendedikasikan ilmu dan kemamuannya untuk merumuskan paradigma ini.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Kami berharap kiranya seluruh civitas akademika Uinersitas ini dapat mendalami dan menerapkannya.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Semoga Allah Swt., senantiasa meridhai uasaha dan aktifitas kita dalam pengembangan keilmuan di Universitas yang kita cinta ini.</span></span></p>\r\n\r\n<p>&nbsp;</p>', '<p>&quot;Salah satu tugas Senat Universitas adalah merumuskan paradigma integrasi keilmuan untuk dapat menghasilkan pengetahuan yang integratif serta dapat berkontribusi bagi pengembangan ilmu, pembangunan peradaban, dan kesejahteraan umat manusia.&nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;Kami berharap kiranya seluruh civitas akademika UIN-SU dapat mendalami dan menerapkannya.&quot;</p>', 'Ketua Senat Universitas Islam Negeri Sumatera Utara Medan', '5d224193692e5.png', 'Prof. Dr. H. Moh. Hatta', '2019-07-07 19:01:39', '2019-11-10 17:42:03'),
(3, 'KATA PENGANTAR  KETUA TIM PENYUSUN', '<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Adalah suatu hal yang tidak dapat dipungkiri dan dielakkan bahwa problema peradaban dan tantangan serta kebutuhan hidup umat manusia terus berkembang, dan bahkan sering terjadi tanpa diperhitungkan (<em>unpredictability</em>) sebelumnya, sebagai akibat dari revolusi ilmu pengetahuan dan teknologi.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Dalam perkembangan sejarah itu manusia memerlukan petunjuk (<em>guidance</em>) yang dapat dijadikan pedoman agar mereka dapat menjalani kehidupannya secara baik, sesuai dengan kebutuhan itu sendiri dan diyakini membawa kepada keselamatan, bukan hanya di dunia, akan tetapi sampai dihadirat Tuhan kelak.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Petunjuk itu diharapkan muncul dari institusi yang paling dapat membaca perubahan dan paling intens mencari solusi bagi persoalan-persoalan umat manusia yaitu perguruan tinggi, meskipun harus diakui bahwa sejumlah perguruan tinggiakibat sistem keilmuan yang dikembangkan belum dapat memenuhi harapan&nbsp;tersebut; sebagian dapat mengawal perkembangan kehidupan dan memberi solusi bagi umat manusia, akan tetapi tidak dapat&nbsp; menguatkan spiritual dan memberi harapan bagi keselamatan dihari kemudian. Pada saat yanag sama ada perguruan tinggi yang secara intens memberi solusi bagi problema spiritual, namun lemah dalam menjawab berbagai persoalan kehidupan umat manusia.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Menyadari hal itu sejumlah perguruan tinggi Islam melakukan transformasi menjadi universitas dengan perluasan bidang ilmu pengetahuan yang dikembangkan dengan harapan dapat memberi solusi bagi problema peradaban dan kemanusiaan serta dapat memberi harapan akan kualitas spiritual dan keselamatan dihari kemudian.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Transformasi tersebut secara tak terelakkan membutuhkan paradigma dan <em>guidance </em>pengembangan ilmu pengetahuan yang integratif sehingga dapat menjawab harapan umat manusia.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Memang diskurusus integrasi ilmu (<em>integration of knowledge</em>) berjalan sudah demikian lama dan cenderung melelahkan. Namun penerapannya belum seiring dengan harapan mengenainya, atau belum sejalan dengan mulianya cita-cita tersebut. Sebenarnya dalam Konferensi Pendidikan Muslim Dunia pertama tahun 1977 di King Abdul Aziz University, diskusi telah sampai pada tahap implementasinya. Namun realisasinya hingga kini belum&nbsp; menunjukkan hasil yang memadai di dunia Islam.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Menyadari hal tersebut Universitas Islam Negeri (UIN) Sumatera Utara merumuskan paradigm pengembangan dan <em>guidance</em> penerapannya dalam menjawab tantangan dan harapan umat manusia sebagaimana terdapat dalam buku ini.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Paradigma dan <em>guidance</em> tersebut diharapan dapat diterapkan secara sungguh-sungguh dan konsisten di segala lini proses pelaksanaan Tri Darma perguruan tinggi di lingkungan Universitas Islam Negeri (UIN) Sumatera Utara.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Atas selesainya rumusan ini kami mengucapkan terima kasih kepada seluruh anggota tim dan <em>support </em>yang demikian penting dari pimpinan Universitas Islam Negeri (UIN) Sumatera Utara dan Senat Universitas.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Semoga Allah Swt., selalu mencurahkaan rahmat dan karunia-Nya bagi keberjayaan universitas ini. <em>Amin.</em></span></span></p>\r\n\r\n<p>&nbsp;</p>', '<p>&quot;Problema peradaban dan tantangan hidup manusia terus mengalami transformasi akibat revolusi ilmu pengetahuan dan teknologi. Menyadari hal itu UIN-SU merumuskan paradigma integrasi ilmu dan guidance penerapannya dalam menjawab tantangan dan harapan umat manusia.&nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;Paradigma dan guidance ini diharapkan dapat diterapkan secara sungguh-sungguh dan konsisten di segala lini proses pelaksanaan Tri Darma perg. tinggi di lingkungan UIN-SU Medan&quot;.</p>', 'Ketua Tim Penyusun', '5d22424d3e06f.png', 'Prof. Dr. H. Syahrin Harahap, MA', '2019-07-07 19:04:45', '2019-11-10 17:41:51');

